'',
'           -- this needs to be changed to something better',
'           if c2.mandatory_yn = ''Y'' then ',
'              sys.htp.p('' *'');',
'              l_mand := ''Y'';',
'           end if;',
'           ',
'           if c2.QUESTION_TYPE in (''TEXTAREA'',''TEXT'') then',
'               sys.htp.p(''</label>'');',
'           end if;',
'           ',
'           sys.htp.prn(''</p>'');',
'',
'           for c3 in (',
'              select image_height, image_width',
